mapper.update:
UPDATE review SET REV_STAR = #{rev_star}, REV_COMMENT = #{rev_comment, jdbcType=VARCHAR}, REV_TITLE = #{rev_title, jdbcType=VARCHAR},
rev_date = sysdate
WHERE REV_NO = #{rev_no}
and  USER_EMAIL = #{user_email,jdbcType=VARCHAR}

mapper.select:
SELECT review.*
FROM review
WHERE review.rst_id = #{rst_id}
